<?php	global $tmpl;	$tmpl -> addStylesheet('styles','blocks/content_home/assets/css');	$Itemid = 7;	FSFactory::include_class('fsstring');?>	<?php if(count($data)){  ?><div class="row">        <?php $i=0;            foreach($data as $item){                $link_content = FSRoute::_("index.php?module=contents&view=content&id=".$item->id."&code=".$item->alias."&ccode=".$item-> category_alias."&Itemid=$Itemid");            $image = URL_ROOT.str_replace('/original/','/original/',$item->image);        ?>        <div class="col-sm-6 col-xs-12">            <div class="item row-item">                <?php if($item->image){ ?>                    <img class="img-responsive fl-right" src="<?php echo $image; ?>" alt="<?php echo $item->title; ?>" />                <?php } ?>                <h3 class="title">                    <a href="<?php echo $link_content; ?>" title="<?php echo $item->title; ?>">                        <?php echo $item->title; ?>                        <i class="fa fa-angle-right"></i>                    </a>                </h3>            </div>        </div>        <?php $i++; } ?>  </div><?php } ?>                 