<?php    global $tmpl;     $tmpl -> addStylesheet('styles','blocks/content_home/assets/css');    $total = count($data);?><?php if($total){ ?>	<?php 		for($i = 0; $i < $total; $i ++ ){			$item = $data[$i];			$link = FSRoute::_("index.php?module=contents&view=content&id=".$item->id."&code=".$item->alias."&ccode=".$item-> category_alias);            $image_resized = URL_ROOT.str_replace('/original/', '/smalls/', $item->image);			?>                <?php if($i==0){ ?>				<a class="col-news fl-left" href='<?php echo  $link;?>' title='<?php echo $item ->title;?>'>					<img class="img-responsive fl-left" alt="" src="<?php echo $image_resized;?>" />                    <h3 class="newslist-item"><?php echo getWord(15,$item->title);?></h3>                    <p class=""><?php echo date('d/m/Y | H:i',strtotime($item->created_time)); ?></p>				</a>				<?php }else{ ?>                <h3 class="col-news-item">                    <a href='<?php echo  $link;?>' title='<?php echo $item ->title;?>'><?php echo getWord(15,$item->title);?></a>                </h3>                <?php } ?>	<?php } ?><?php } ?>